@njit(parallel=True, fastmath=True)
def compute_energy_s1(xs):
    n = xs.size
    if n < 2:
        return 0.0
    ene = 0.0
    for i in prange(n):
        for j in range(i + 1, n):
            dx = abs(xs[i] - xs[j])
            if dx < 1e-12:
                dx = 1e-12
            ene += 1.0 / dx
    return ene

@njit(parallel=True, fastmath=True)
def compute_grad_s1(xs):
    n = xs.size
    grad = np.zeros(n)
    if n < 2:
        return grad
    # parallelized over i
    for i in prange(n):
        for j in range(i + 1, n):
            dx = xs[i] - xs[j]
            adx = abs(dx)
            if adx < 1e-12:
                adx = 1e-12
            inv = 1.0 / adx
            g = -np.sign(dx) * inv * inv
            grad[i] += g
            grad[j] -= g
    return grad
