# Vectorized numpy implementations for s=1; faster for small n without Numba overhead
def compute_energy_s1(xs):
    n = xs.size
    if n < 2:
        return 0.0
    dx = np.abs(xs[:, None] - xs[None, :])
    # avoid division by zero
    dx[dx < 1e-12] = 1e-12
    # sum over upper triangle
    return np.sum(np.triu(1.0 / dx, k=1))

def compute_grad_s1(xs):
    n = xs.size
    if n < 2:
        return np.zeros(n)
    dx = xs[:, None] - xs[None, :]
    adx = np.abs(dx)
    adx[adx < 1e-12] = 1e-12
    inv2 = 1.0 / (adx * adx)
    g_mat = -np.sign(dx) * inv2
    # zero diagonal contributions
    np.fill_diagonal(g_mat, 0.0)
    # sum rows to get gradient
    return np.sum(g_mat, axis=1)
