import numpy as np
from numba import njit, prange

def equally_spaced(n: int) -> np.ndarray:
    """Return n equally spaced points in [0,1]. Center at 0.5 for n==1."""
    if n == 0:
        return np.empty(0)
    if n == 1:
        return np.array([0.5])
    return np.linspace(0.0, 1.0, n)

def jittered_baseline(n: int, seed: int = 0, jitter: float = 1e-3) -> np.ndarray:
    """Return n equally spaced points in [0,1] with small random jitter."""
    rng = np.random.default_rng(seed)
    xs = equally_spaced(n)
    if n > 1:
        xs = np.clip(xs + rng.uniform(-jitter, jitter, size=n), 0.0, 1.0)
        xs.sort()
    return xs

@njit(cache=True, parallel=True, fastmath=True)
def compute_energy(xs: np.ndarray, s: float = 1.0) -> float:
    """Compute Riesz s-energy via direct double loop (numba accelerated)."""
    n = xs.size
    if n < 2:
        return 0.0
    ene = 0.0
    for i in prange(n):
        # Only sum j>i to avoid double counting
        for j in range(i + 1, n):
            dx = xs[j] - xs[i]
            ene += dx ** (-s)
    return ene

@njit(cache=True, parallel=True, fastmath=True)
def compute_grad(xs: np.ndarray, s: float = 1.0) -> np.ndarray:
    """Compute gradient of Riesz s-energy via two-range loops (numba accelerated)."""
    n = xs.size
    grad = np.zeros(n)
    if n < 2:
        return grad
    for i in prange(n):
        g = 0.0
        xi = xs[i]
        # contributions from all x_j with j < i
        for j in range(i):
            # dx = xi - xs[j] > 0
            g -= s * ( (xi - xs[j]) ** (-s - 1) )
        # contributions from all x_j with j > i
        for j in range(i + 1, n):
            # dx = xs[j] - xi > 0, sign flips
            g += s * ( (xs[j] - xi) ** (-s - 1) )
        grad[i] = g
    return grad

@njit(cache=True)
def optimize(xs: np.ndarray, s: float = 1.0, lr: float = 1e-2, iters: int = 2000, tol: float = 1e-8, momentum: float = 0.9) -> np.ndarray:
    # Nesterov-accelerated projected gradient descent with inline backtracking sort/clip
    n = xs.shape[0]
    xs_curr = xs.copy()
    xs_prev = xs.copy()
    prev_energy = compute_energy(xs_curr, s)
    for k in range(1, iters + 1):
        # momentum extrapolate
        y = xs_curr + momentum * (xs_curr - xs_prev)
        # gradient + norm²
        g = compute_grad(y, s)
        grad_norm2 = 0.0
        for i in range(n):
            grad_norm2 += g[i] * g[i]
        if grad_norm2 < tol * tol:
            break
        step = lr
        # backtracking
        for _ in range(8):
            xs_new = y - step * g
            # clip
            for i in range(n):
                if xs_new[i] < 0.0:
                    xs_new[i] = 0.0
                elif xs_new[i] > 1.0:
                    xs_new[i] = 1.0
            # insertion sort
            for i in range(1, n):
                key = xs_new[i]
                j = i - 1
                while j >= 0 and xs_new[j] > key:
                    xs_new[j+1] = xs_new[j]
                    j -= 1
                xs_new[j+1] = key
            new_energy = compute_energy(xs_new, s)
            if new_energy < prev_energy:
                xs_prev = xs_curr
                xs_curr = xs_new
                prev_energy = new_energy
                break
            step *= 0.5
        else:
            # no sufficient decrease
            momentum *= 0.5
            lr *= 0.5
            continue
        lr *= 0.995
    # final sort
    for i in range(1, n):
        key = xs_curr[i]
        j = i - 1
        while j >= 0 and xs_curr[j] > key:
            xs_curr[j+1] = xs_curr[j]
            j -= 1
        xs_curr[j+1] = key
    return xs_curr

def main():
    n = 20
    s = 1.0
    # initialize with equally spaced points
    xs_init = jittered_baseline(n, seed=0, jitter=1e-3)
    # refine via projected gradient descent
    xs_local = optimize(xs_init, s, lr=1e-2, iters=500, momentum=0.9)
    # report final energy
    print("Final Riesz s-energy:", compute_energy(xs_local, s))
    return xs_local

if __name__ == "__main__":
    main()
