@njit(parallel=True, fastmath=True)
def compute_energy_s1(xs):
    n = xs.size
    if n < 2:
        return 0.0
    ene = 0.0
    for i in range(n):
        for j in range(i + 1, n):
            dx = abs(xs[i] - xs[j])
            if dx < 1e-12:
                dx = 1e-12
            ene += 1.0 / dx
    return ene
