@njit(parallel=True, fastmath=True)
def compute_grad_s1(xs):
    n = xs.size
    grad = np.zeros(n)
    if n < 2:
        return grad
    # parallelized over i
    for i in prange(n):
        for j in range(i + 1, n):
            dx = xs[i] - xs[j]
            adx = abs(dx)
            if adx < 1e-12:
                adx = 1e-12
            # derivative of 1/|dx| is -sign(dx)/|dx|^2
            g = -np.sign(dx) / (adx * adx)
            grad[i] += g
            grad[j] -= g
    return grad
