import numpy as np
from numba import njit, prange
from scipy.optimize import minimize

def equally_spaced(n: int) -> np.ndarray:
    """Return n equally spaced points on [0,1] using numpy.linspace."""
    # numpy.linspace covers n=0, n=1, and n>1 uniformly
    return np.linspace(0.0, 1.0, n)

def jittered_baseline(n: int, seed: int = 0, jitter: float = 1e-3):
    """A simple baseline: equal grid + tiny jitter (still clipped to [0,1])."""
    rng = np.random.default_rng(seed)
    xs = equally_spaced(n)
    if n > 1:
        xs += rng.uniform(-jitter, jitter, size=n)
        xs = np.clip(xs, 0.0, 1.0)
    # Sort once at the end to ensure a monotonic sequence
    xs.sort()
    return xs

def chebyshev_nodes(n: int) -> np.ndarray:
    """Return n Chebyshev nodes scaled to [0,1], clustering at endpoints."""
    if n == 0:
        return np.array([])
    if n == 1:
        return np.array([0.5])
    k = np.arange(n)
    xs = 0.5 * (1 - np.cos((2*k + 1)/(2*n) * np.pi))
    return xs

@njit(parallel=True, fastmath=True)
def compute_energy(xs: np.ndarray, s: float = 1.0) -> float:
    """Compute Riesz s-energy via direct double loop (numba accelerated), clamped."""
    n = xs.size
    if n < 2:
        return 0.0
    ene = 0.0
    for i in prange(n):
        # Only sum j>i to avoid double counting
        for j in range(i + 1, n):
            dx = abs(xs[i] - xs[j])
            # clamp tiny distances to avoid infinities
            if dx < 1e-12:
                dx = 1e-12
            ene += dx ** (-s)
    return ene

@njit(parallel=True, fastmath=True)
def compute_grad(xs: np.ndarray, s: float = 1.0) -> np.ndarray:
    """Compute gradient of Riesz s-energy using symmetric updates, clamped."""
    n = xs.size
    grad = np.zeros(n)
    if n < 2:
        return grad
    # Only loop over i<j and accumulate symmetrically
    for i in prange(n):
        for j in range(i + 1, n):
            dx = xs[i] - xs[j]
            adx = abs(dx)
            # clamp tiny distances
            if adx < 1e-12:
                adx = 1e-12
            # derivative of adx^{-s} is -s * adx^{-s-1} * sign(dx)
            g = -s * (adx ** (-s - 1)) * np.sign(dx)
            grad[i] += g
            grad[j] -= g
    return grad

# Removed Hessian diagonal preconditioners to simplify the codebase (unused in current solver).

# specialized routines for s=1.0
@njit(parallel=True, fastmath=True)
def compute_energy_s1(xs):
    n = xs.size
    if n < 2:
        return 0.0
    ene = 0.0
    for i in prange(n):
        for j in range(i + 1, n):
            dx = abs(xs[i] - xs[j])
            if dx < 1e-12:
                dx = 1e-12
            ene += 1.0 / dx
    return ene

@njit(parallel=True, fastmath=True)
def compute_grad_s1(xs):
    n = xs.size
    grad = np.zeros(n)
    if n < 2:
        return grad
    # parallelized over i
    for i in prange(n):
        for j in range(i + 1, n):
            dx = xs[i] - xs[j]
            adx = abs(dx)
            if adx < 1e-12:
                adx = 1e-12
            # derivative of 1/|dx| is -sign(dx)/|dx|^2
            g = -np.sign(dx) / (adx * adx)
            grad[i] += g
            grad[j] -= g
    return grad

# Precompile the s=1 energy and gradient to avoid closure overhead in optimize()
def f_s1(x):
    return compute_energy_s1(x)

def grad_s1_wrapped(x):
    return compute_grad_s1(x)

# specialized optimize for s=1 to use precompiled s1 routines and avoid arg overhead
def optimize(xs: np.ndarray, s: float = 1.0, tol: float = 1e-12) -> np.ndarray:
    """Use L-BFGS-B to optimize Riesz s-energy, dispatching to s=1 kernels when possible."""
    bounds = [(0.0, 1.0)] * xs.size
    if s == 1.0:
        res = minimize(f_s1,
                       xs,
                       method='L-BFGS-B',
                       jac=grad_s1_wrapped,
                       bounds=bounds,
                       options={'ftol': min(tol, 1e-15), 'maxiter': 10000})
    else:
        res = minimize(compute_energy,
                       xs,
                       args=(s,),
                       method='L-BFGS-B',
                       jac=compute_grad,
                       bounds=bounds,
                       options={'ftol': min(tol, 1e-15), 'maxiter': 10000})
    return np.sort(res.x)

def main():
    n = 20
    s = 1.0
    # for s=1 the equally spaced grid is already global optimum; skip restarts
    if s == 1.0:
        xs_local = optimize(equally_spaced(n), s)
        best_e = compute_energy_s1(xs_local)
    else:
        # multi-start loop for s≠1
        best_e = np.inf
        best_xs = None
        num_restarts = 100
        for seed in range(num_restarts):
            if seed == 0:
                xs_init = chebyshev_nodes(n)
            elif seed % 3 == 0:
                xs_init = np.sort(np.random.default_rng(seed).uniform(0.0, 1.0, size=n))
            else:
                jitter = 2e-1 * (0.5 ** ((seed - 1)//4))
                xs_init = jittered_baseline(n, seed=seed, jitter=jitter)
            xs_candidate = optimize(xs_init, s)
            e_candidate = compute_energy(xs_candidate, s)
            if e_candidate < best_e:
                best_e = e_candidate
                best_xs = xs_candidate
        xs_local = best_xs
    print("Final Riesz s-energy:", best_e)
    return xs_local
